(function($,c,d){if(typeof $==='undefined')return console.log('Please require jQuery before using upload button!');$(c).ready(function($){if($('#photobox_options_upload_image_button').length==0)return;console.log('PhotoBox setting ready!',$);var b,clicked_on_imgbtn=false;$(c.body).on('click.tgmOpenMediaManager','#photobox_options_upload_image_button',function(e){e.preventDefault();if(b){b.open();return}b=wp.media.frames.tgm_media_frame=wp.media({className:'media-frame tgm-media-frame',frame:'select',multiple:false,title:'Upload Image',library:{type:'image'},button:{text:'Use this image'}});b.on('select',function(){var a=b.state().get('selection').first().toJSON();jQuery('#photobox_options_image_id').val(a.id);jQuery('#photobox_options_image_thumb').html('<img src="'+a.url+'" height=100 alt=""/>')});b.open()});$('#photobox_options_remove_image_button').click(function(e){e.preventDefault();$('#photobox_options_image_id').attr('value','');$('#photobox_options_image_thumb').html('')});$('#photobox_options_upload_image_button').click(function(){formfield=jQuery('#photobox_options_image_id').attr('name');clicked_on_imgbtn=true;console.log(tb_show);tb_show('Add Image','media-upload.php?type=image&amp;TB_iframe=true');return false})})})(jQuery,document,window);